export const SoftwareTools: React.FC = () => {
  const [activeTab, setActiveTab] = useState(toolsData[0].appName);

  const activeTools = toolsData.find(app => app.appName === activeTab)?.tools || [];

  return (
    <div className="flex flex-col h-full bg-gray-50">
      <div className="p-2 bg-white border-b border-gray-200">
        <div className="flex space-x-1 bg-gray-100 p-1 rounded-lg">
          {toolsData.map(app => (
            <button
              key={app.appName}
              onClick={() => setActiveTab(app.appName)}
              className={`w-full px-3 py-2 text-sm font-medium rounded-md transition-colors duration-200 focus:outline-none ${
                activeTab === app.appName
                  ? 'bg-white text-indigo-600 shadow'
                  : 'text-gray-500 hover:bg-gray-200'
              }`}
            >
              {app.appName}
            </button>
          ))}
        </div>
      </div>
      <div className="flex-1 overflow-y-auto p-4 space-y-3">
        {activeTools.map((tool, index) => (
          <div key={index} className="bg-white p-4 rounded-lg border border-gray-200 shadow-sm">
            <h3 className="font-bold text-indigo-700">{tool.name}</h3>
            <p className="mt-1 text-sm text-gray-600">{tool.description}</p>
            {tool.image && (
                <div className="mt-3 border-t pt-3">
                    <img src={tool.image} alt={`Minh họa cho ${tool.name}`} className="rounded-md border border-gray-300 w-full" />
                </div>
            )}
          </div>
        ))}
         {activeTools.length === 0 && (
            <p className="text-center text-gray-400 mt-8">Chưa có công cụ nào được thêm vào đây.</p>
         )}
      </div>
    </div>
  );
};
